// Extract data from webhook
const taskId = $input.first().json.task_id;
const role = $input.first().json.role;
const description = $input.first().json.description;
const content = $input.first().json.content;

// Log the received data
console.log('Received social media task:', { taskId, role, description });

// Format content for social media
const socialMediaPost = {
  platform: 'twitter',
  content: content.substring(0, 280), // Twitter character limit
  hashtags: ['#launch', '#product', '#AI'],
  scheduled: false
};

return {
  task_id: taskId,
  role: role,
  description: description,
  social_media_post: socialMediaPost,
  status: 'processed',
  timestamp: new Date().toISOString()
};

// Simulate posting to social media
const data = $input.first().json;

// In a real implementation, this would connect to Twitter API, LinkedIn API, etc.
console.log('Posting to social media:', data.social_media_post);

// Simulate API response
const response = {
  success: true,
  post_id: 'sm_' + Math.random().toString(36).substr(2, 9),
  platform: data.social_media_post.platform,
  content: data.social_media_post.content,
  posted_at: new Date().toISOString(),
  engagement: {
    likes: 0,
    shares: 0,
    comments: 0
  }
};

return {
  ...data,
  social_media_response: response,
  execution_status: 'completed'
};
